import { CustomPicker } from 'react-color';
import { EditableInput, Hue, Saturation } from 'react-color/lib/components/common';
import reactCSS from 'reactcss';
import * as color from 'react-color/lib/helpers';

function ColorPicker(props) {

    var handleChange = (data, e) => {
        if(data.hex) {
            color.isValidHex(data.hex) && props.onChange({
                hex: data.hex,
                source: 'hex'
            }, e);
        }
        else if(data.r || data.g || data.b) {
            props.onChange({
                r: data.r || props.rgb.r,
                g: data.g || props.rgb.g,
                b: data.b || props.rgb.b,
                a: '1',
                source: 'rgb'
            }, e);
        }
    };

    return(
        <div className="ColorPicker">
            <input id="color-name-input" type="text" />
            <SaturationBlock hsl={props.hsl} hsv={props.hsv} onChange={props.onChange}/>
            <EditableInput label="hex" value={props.hex} onChange={handleChange} />
            <EditableInput label="r" value={props.r} onChange={handleChange} />
            <EditableInput label="g" value={props.g} onChange={handleChange} />
            <EditableInput label="b" value={props.b} onChange={handleChange} />
            <HueSlider hsl={props.hsl} onChange={props.onChange} />
            <ColorPreview hex={props.hex}/>
        </div>
    );
}

function SaturationBlock({hsl, hsv, onChange}) {
    const style = reactCSS({
        'default': {
            saturation: {
                position: "relative",
                width: "50px",
                height: "50px"
            }            
        }
    });

    return(
        <div className="SaturationBlock" style={style.saturation}>
            <Saturation hsl={hsl} hsv={hsv} onChange={onChange} />
        </div>
    );
}

function HueSlider({hsl, onChange}) {
    const style = reactCSS({
        'default' : {
            hue: {
                position: "relative",
                width: "100px",
                height: "10px"
            }
        }
    });

    return(
        <div className="HueSlider" style={style.hue}>
            <Hue hsl={hsl} onChange={onChange} />
        </div>
    );
}

function ColorPreview({ hex }) {
    const style = reactCSS({
        'default': {
            color: {
                background: hex,
                position: "relative",
                width: "50px",
                height: "50px"
            }
        }
    });

    return(
        <div className="ColorPreview" style={style.color} />
    );
}


export default CustomPicker(ColorPicker);
