import { Project, projectsList, writeProjects, fireRerender } from './ProjectClass';

export function addProject() {
    const projectName = document.getElementById("new-project-name-input").value;
    projectsList.push(new Project(projectName, 0, []));
    writeProjects();
    fireRerender();
}

export function deleteProject(id) {
    projectsList.splice(id, 1);
    writeProjects();
    fireRerender();
}
