import ColorPicker from "../../ColorPicker";
import { useState } from "react";
import { addColor, saveColor } from "../../ColorPickerScripting";

function ColorPickerModal(props) {
    const [color, changeColor] = useState(props.colorList[props.colorId] ? props.colorList[props.colorId].color : "");

    var handleChange = (color) => {
        changeColor(color.hex);
    }

    var name = props.colorList[props.colorId] ? props.colorList[colorId].name : "New Color";

    return(
        <div className="ColorPickerModal">
            <div className="ModalBackground">
                <div className="ModalContainer">
                    <h1>{name}</h1>
                    <input id="color-name-input" type="text" defaultValue={name}/>
                    <ColorPicker color={color} onChange={handleChange}/>
                    <button onClick={() => {props.color ? saveColor(props.color, color, props.isProject) : addColor(props.colorList, color, props.isProject); props.modalOpen(false);}}>Save</button>
                    <button onClick={() => props.modalOpen(false)}>Cancel</button>
                </div>
            </div>
        </div>
    );
}

export default ColorPickerModal;
