import { projectsList } from "../ProjectsPage/ProjectClass";

export function sortArray(array) {
    const sortValue = document.getElementById("sort-dropdown").value;
    array.join();
    switch(sortValue) {
        case "0": {
            return array.sort(sortAlphDesc);
        }
        case "1": {
            return array.sort(sortAplhAsc);
        }
        case "2": {
            return array.sort(sortIdAsc);
        }
        case "3": {
            return array.sort(sortIdDesc);
        }
        case "4": {
            return array.sort(sortColorAsc);
        }
        case "5": {
            return array.sort(sortColorDesc);
        }
        default: {}
    }
}

export function sortButton_onClick(array, changeArray) {
    const sortValue = document.getElementById("sort-dropdown").value;
    array.join();
    switch(sortValue) {
        case "0": {
            changeArray(array.sort(sortAlphDesc));
            break;
        }
        case "1": {
            changeArray(array.sort(sortAplhAsc));
            break;
        }
        case "2": {
            changeArray(array.sort(sortIdAsc));
            break;
        }
        case "3": {
            changeArray(array.sort(sortIdDesc));
            break;
        }
        case "4": {
            changeArray(array.sort(sortColorAsc));
            break;
        }
        case "5": {
            changeArray(array.sort(sortColorDesc));
            break;
        }
        default: {}
    }

    saveSortValue();

    console.log(projectsList);

}

function saveSortValue(){
    const sortValue = document.getElementById("sort-dropdown").value;
    window.localStorage.setItem("sort", sortValue);
}

export function readSortValue() {
    return window.localStorage.getItem("sort");
}

function sortAplhAsc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0;
}

function sortAlphDesc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(aName < bName) return 1;
    if(aName > bName) return -1;
    return 0;
}

function sortIdAsc(a, b) {
    if(a.id < b.id) return 1;
    if(a.id > b.id) return -1;
    return 0;
}

function sortIdDesc(a, b) {
    if(a.id > b.id) return 1;
    if(a.id < b.id) return -1;
    return 0;
}

function sortColorAsc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(a.colorId > b.colorId) return 1;
    if(a.colorId < b.colorId) return -1;
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0;
}

function sortColorDesc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(a.colorId < b.colorId) return 1;
    if(a.colorId > b.colorId) return -1;
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0; 
}


function reloadProjects(){
    const event = new CustomEvent("reloadProjects");
    document.dispatchEvent(event);
}

function reloadGoals(){
    const event = new CustomEvent("reloadGoals");
    document.dispatchEvent(event);
}
