import { useEffect, useState } from "react";
import { useParams, lINK } from "react-router-dom"
import { projectsList } from "../ProjectsPage/ProjectClass";
import CreateGoalModal from "./Modals/CreateGoalModal/CreateGoalModal";
import EditGoalModal from "./Modals/EditGoalModal/EditGoalModal";
import DeleteGoalModal from "./Modals/DeleteGoalModal/DeleteGoalModal";
import SavedColorsComponent from "../ColorPickerComponent/SavedColorComponent/SavedColorsComponent";
import reactcss from 'reactcss';
import SortDropdown from "../SortDropdown/SortDropdown";

import './GoalsPageStyles.css';

export function GoalsPage(){
    let {id} = useParams();

    const project = projectsList[id];

    const [createOpen, changeCreateOpen] = useState(false);
    const [editOpen, changeEditOpen] = useState(false);
    const [deleteOpen, changeDeleteOpen] = useState(false);
    const [selectedGoal, changeSelectedGoal] = useState();
    const [reloadState, reload] = useState(0);

    useEffect(() => {
        document.addEventListener("reload", reloadEffect);

        return () => {
            document.removeEventListener("reload", reloadEffect);
        }
    })

    const reloadEffect = () => {
        reload(reloadState + 1);
    }

    return(
        <div className="GoalsPage">
            <Link to={"/project"}>
                <button>Back</button>
            </Link>
            <h1>{project.name + " Goals"}</h1>
            <SortDropdown array={project.goals} />
            <GoalList goals={project.goals} goalColors={project.goalColors} openCreate={changeCreateOpen} openDelete={changeDeleteOpen} openEdit={changeEditOpen} changeSelectedGoal={changeSelectedGoal}/>
            <SavedColorsComponent colors={project.goalColors} isProject={false}/>
            {createOpen && <CreateGoalModal goals={project.goals} colorList={project.goalColors} modalOpen={changeCreateOpen}/>}
            {editOpen && <EditGoalModal goal={project.goals[selectedGoal]} colorList={project.goalColors} modalOpen={changeEditOpen} />}
            {deleteOpen && <DeleteGoalModal goals={project.goals} goalId={selectedGoal} modalOpen={changeDeleteOpen}/>}
        </div>
    );
}

function GoalList(props) {
    var goalListItems = props.goals ? props.goals.map((goal, id) => {
        return(
            <li key={id}>
                <GoalListItem id={id} name={goal.name} status={goal.status} colorId={goal.colorId} goalColors={props.goalColors} openDelete={props.openDelete} openEdit={props.openEdit} changeSelectedGoal={props.changeSelectedGoal}/>
            </li>
        );
    }) : [];

    return(
        <ul className="GoalList">
            {goalListItems}
            <li>
                <button onClick={() => props.openCreate(true)}>+ Add Goal</button>
            </li>
        </ul>
    );
}

function GoalListItem(props) {
    const style = reactcss({
        'default': {
            goalColor: {
                background: props.goalColors[props.colorId].color
            }
        }
    })

    return(
        <div className="GoalListItem">
            <button onClick={() => {props.changeSelectedGoal(props.id); props.openEdit(true);}}>
                {props.colorId !== "0" && <div id="goal-color" style={style.goalColor}/>}
                {props.name + " : " + props.status}
            </button>
            <button onClick={() => {props.changeSelectedGoal(props.id); props.openDelete(true);}}>X</button>
        </div>
    );
}
