import { useState } from 'react';
import ColorPickerModal from '../ColorPicker/Modals/ColorPickerModal/ColorPickerModal';
import { SavedColor } from './SavedColorClass';
import DeleteColorModal from '../ColorPicker/Modals/DeleteColorModal/DeleteColorModal';

function SavedColorsComponent(props) {
    const [newColorOpen, changeNewColorOpen] = useState(false);
    const [deleteColorOpen, changeDeleteColorOpen] = useState(false);
    const [selectedColor, changeSelectedColor] = useState();
    return(
        <div className="SavedColorsComponent">
            <h2>Saved Colors</h2>
            <SavedColorsList colors={props.colors} openNewColor={changeNewColorOpen} openDelete={changeDeleteColorOpen} changeSelectedColor={changeSelectedColor}/>
            {newColorOpen && <ColorPickerModal colorList={props.colors} color={new SavedColor(null, null, "")} modalOpen={changeNewColorOpen} isProject={props.isProject} />}
            {deleteColorOpen && <DeleteColorModal color={selectedColor} modalOpen={changeDeleteColorOpen}/>}
        </div>
    )
}

function SavedColorsList(props) {
    var savedColorsListItems = props.colors.map((color, id) => {
        if(id !== 0) 
            return(
                <li key={id}>
                    <SavedColorsListItem color={color} changeSelectedColor={props.changeSelectedColor}/>
                </li>
            );
            else return null;
    });

    return(
        <ul className="SavedColorsList">
            {savedColorsListItems}
            <button onClick={() => props.openNewColor(true)}>+ Add Color</button>
        </ul>
    )
}

function SavedColorsListItem(props) {
    return(
        <div className="SavedColorsListItem">
            <button>{props.color.name}</button>
            <button onClick={() => {props.changeSelectedColor(props.color); props.openDelete(true);}}>X</button>
        </div>
    );
}

export default SavedColorsComponent;
