export var projectsList = [];

export class Project {
    constructor(name, id, goals, colorId, goalColors) {
        this.name = name;
        this.id = id;
        this.goals = goals;
        this.colorId = colorId;
        this.goalColors = goalColors;
    }
}

export function readProjects() {
        const projectJson = JSON.parse(window.localStorage.getItem('projects'));
        var projects = projectJson ? 
            projectJson.map((project, id) => {
                var colorId = 0;
                var goalColors = [];
                var goals = [];

                return (new Project(project.name, id, goals, colorId, goalColors));
            }) :
            [];
        
        projectsList = projects;
    }

    export function writeProjects() {
        const jsonString = JSON.stringify(projectsList, null, 2);
        window.localStorage.setItem("projects", jsonString);
    }

    export function fireRerender() {
        const rerenderEvent = new Event("rerender");
        window.dispatchEvent(rerenderEvent);
    }
