import { writeProjects } from "../../ProjectsPage/ProjectClass";
import { SavedColor, writeColors } from "../SavedColorComponent/SavedColorClass";

export function addColor(colorList, color, isProject){
    const colorName = document.getElementById("color-name-input").value;
    colorList.push(new SavedColor(colorName, 0, color));

    if(isProject) {
        writeColors();
    }
    else
        writeProjects();
}

export function deleteColor(colorList, color, isProject) {
    colorList.splice(color.id, 1);
    
    if(isProject) {
        writeColors();
    }
    else
        writeProjects();
}

export function saveColor(colorObject, color, isProject) {
    color

    if(isProject) {
        writeColors();
    }
    else
        writeProjects();
}
