import { useState, useEffect } from "react";
import { useParams } from "react-router-dom"
import { projectsList } from "../ProjectsPage/ProjectClass";
import CreateGoalModal from "./Modals/CreateGoalModal/CreateGoalModal";
import EditGoalModal from "./Modals/EditGoalModal/EditGoalModal";
import DeleteGoalModal from "./Modals/DeleteGoalModal/DeleteGoalModal";

export function GoalsPage(){
    let {id} = useParams();

    const project = projectsList[id];

    const [dummyState, rerender] = useState(1);
    const [createOpen, changeCreateOpen] = useState(false);
    const [editOpen, changeEditOpen] = useState(false);
    const [deleteOpen, changeDeleteOpen] = useState(false);

    useEffect(() =>{
        document.addEventListener("rerender", rerenderListener);

        return () => {
            document.removeEventListener("rerender", rerenderListener);
        }
    });

    const rerenderListener = () => {
        rerender(dummyState + 1);
    };

    return(
        <div className="GoalsPage">
            <h1>{project.name + " Goals"}</h1>
            <GoalList goals={project.goals} openCreate={changeCreateOpen}/>
            {createOpen && <CreateGoalModal goals={project.goals} modalOpen={changeCreateOpen}/>}
            {editOpen && <EditGoalModal />}
            {deleteOpen && <DeleteGoalModal />}
        </div>
    );
}

function GoalList(props) {
    var goalListItems = props.goals ? props.goals.map((goal, id) => {
        return(
            <li key={id}>
                <GoalListItem name={goal.name}/>
            </li>
        );
    }) : [];

    return(
        <ul className="GoalList">
            {goalListItems}
            <button onClick={() => props.openCreate(true)}>+ Add Goal</button>
        </ul>
    );
}

function GoalListItem(props) {
    return(
        <div className="GoalListItem">
            <button>{props.name}</button>
        </div>
    );
}
