import { projectsList, readProjects } from './ProjectClass';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { addProject } from './ProjectsPageScripting';
import DeleteProjectModal from './Modals/DeleteProjectModal/DeleteProjectModal';
import CreateProjectModal from './Modals/CreateProjectModal/CreateProjectModal';

export function ProjectsPage() {
    const [dummyState, rerender] = useState(1);
    const [deleteOpen, changeDeleteOpen] = useState(false);
    const [editOpen, changeEditOpen] = useState(false);
    const [createOpen, changeCreateOpen] = useState(false);
    const [selectedProject, changeSelectedProject] = useState();
    
    readProjects();

    useEffect(() =>{
        document.addEventListener("rerender", rerenderListener);

        return () => {
            document.removeEventListener("rerender", rerenderListener);
        }
    });

    const rerenderListener = () => {
        rerender(dummyState + 1);
    };

    return(
        <div className="ProjectsPage">
            <h1>Projects</h1>
            <ProjectList changeSelectedProject={changeSelectedProject} openDelete={changeDeleteOpen}/>
            {deleteOpen && <DeleteProjectModal projectId={selectedProject} modalOpen={changeDeleteOpen}/>}
            {createOpen && <CreateProjectModal />}
        </div>
    );
}

function ProjectList(props) {
    var projectListItems = projectsList ?
        projectsList.map((project, id) => {
            return(
                <li key={id}>
                    <ProjectListItem name={project.name} id={id} changeSelectedProject={props.changeSelectedProject} openDelete={props.openDelete}/>
                </li>
            );
        }) :
        [];

    return(
        <ul className="ProjectList">
            {projectListItems}
            <li>
                <button>+ Add Project</button>
            </li>
        </ul>
    );
}

function ProjectListItem(props) {


    return(
        <div className="ProjectListItem">
            <Link to={"/project/" + props.id}>
                    <button>
                        {props.name}
                    </button>
            </Link>
            <button>...</button>
            <button onClick={() => {props.changeSelectedProject(props.id); props.openDelete(true);}}>X</button>
        </div>
    );
}

function CreateProject(props) {
    const [createProject, changeCreateProject] = useState(false);

    return(
        <div className="CreateProject">
            {
                createProject ?
                <>
                    <input id="new-project-name-input" type="text" defaultValue="newProject" />
                    <button onClick={() => {addProject(); changeCreateProject(false);}}>Create</button>
                    <button onClick={() => changeCreateProject(false)}>Cancel</button>
                </> :
                <button onClick={() => changeCreateProject(true)}>+ Add Project</button>
            }
        </div>
    );
}
