import { Goal } from '../GoalsPage/GoalClass';
import { writeProjects } from '../ProjectsPage/ProjectClass';

export function addGoal(goals){
    const goalName = document.getElementById("new-goal-name-input").value;
    const goalStatus = "N/A";
    goals.push(new Goal(goalName, 0, goalStatus, 0));
    writeProjects();
}
