import { projectsList } from "../ProjectsPage/ProjectClass";

export function sortArray(array, isProject, reloadPage) {
    const sortValue = document.getElementById("sort-dropdown").value;
    switch(sortValue) {
        case "0": {
            array.sort(sortAlphDesc);
            break;
        }
        case "1": {
            array.sort(sortAplhAsc);
            break;
        }
        case "2": {
            array.sort(sortIdAsc);
            break;
        }
        case "3": {
            array.sort(sortIdDesc);
            break;
        }
        case "4": {
            array.sort(sortColorAsc);
            break;
        }
        case "5": {
            array.sort(sortColorDesc);
            break;
        }
        default: {}
    }

    for(var i = 0; i < array.length; i++ ){
        projectsList[i] = array[i];
    }

    reloadPage();

}

function sortAplhAsc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0;
}

function sortAlphDesc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(aName < bName) return 1;
    if(aName > bName) return -1;
    return 0;
}

function sortIdAsc(a, b) {
    if(a.id < b.id) return 1;
    if(a.id > b.id) return -1;
    return 0;
}

function sortIdDesc(a, b) {
    if(a.id > b.id) return 1;
    if(a.id < b.id) return -1;
    return 0;
}

function sortColorAsc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(a.colorId > b.colorId) return 1;
    if(a.colorId < b.colorId) return -1;
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0;
}

function sortColorDesc(a, b) {
    const aName = a.name.toUpperCase();
    const bName = b.name.toUpperCase();
    if(a.colorId < b.colorId) return 1;
    if(a.colorId > b.colorId) return -1;
    if(aName > bName) return 1;
    if(aName < bName) return -1;
    return 0; 
}


function reloadProjects(){
    const event = new CustomEvent("reloadProjects");
    document.dispatchEvent(event);
}

function reloadGoals(){
    const event = new CustomEvent("reloadGoals");
    document.dispatchEvent(event);
}
