import { writeProjects } from "../../ProjectsPage/ProjectClass";
import { savedColors, SavedColor, writeColors } from "./SavedColorClass";

export function addColor(color, isProject){
    const colorName = document.getElementById("color-name-input").value;
    colorList.push(new SavedColor(colorName, 0, color));

    if(isProject) {
        writeColors();
    }
    else
        writeProjects();
}
