import { projectsList } from './ProjectClass';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import DeleteProjectModal from './Modals/DeleteProjectModal/DeleteProjectModal';
import CreateProjectModal from './Modals/CreateProjectModal/CreateProjectModal';
import EditProjectModal from './Modals/EditProjectModal/EditProjectModal';
import { savedColors } from '../ColorPickerComponent/SavedColorComponent/SavedColorClass';
import SavedColorsComponent from '../ColorPickerComponent/SavedColorComponent/SavedColorsComponent';
import reactcss from 'reactcss';
import SortDropdown from '../SortDropdown/SortDropdown';

import './ProjectsPageStyles.css';
import { ReadData } from '../PageLoad';

export function ProjectsPage() {
    const [deleteOpen, changeDeleteOpen] = useState(false);
    const [editOpen, changeEditOpen] = useState(false);
    const [createOpen, changeCreateOpen] = useState(false);
    const [selectedProject, changeSelectedProject] = useState();
    const [reloadState, reload] = useState(0);

    ReadData();

    useEffect(() => {
        document.addEventListener("reload", reloadEffect);
        ReadData();

        return () => {
            document.removeEventListener("reload", reloadEffect);
        }
    });

    const reloadEffect = () => {
        reload(reloadState + 1);
    }

    return(
        <div className="ProjectsPage">
            <div id="left-container">
                <h1>Projects</h1>
                <div id="sort-dropdown-alignment">
                    <SortDropdown array={projectsList} />
                </div>
                <ProjectList changeSelectedProject={changeSelectedProject} openDelete={changeDeleteOpen} openCreate={changeCreateOpen} openEdit={changeEditOpen}/>
            </div>
            
            <div id="right-container">
                <SavedColorsComponent colors={savedColors} isProject={true}/>
            </div>

            {deleteOpen && <DeleteProjectModal projectId={selectedProject} modalOpen={changeDeleteOpen}/>}
            {createOpen && <CreateProjectModal modalOpen={changeCreateOpen}/>}
            {editOpen && <EditProjectModal projectId={selectedProject} modalOpen={changeEditOpen}/>}
        </div>
    );
}

function ProjectList(props) {
    var projectListItems = projectsList ? projectsList.map((project, id) => {
        console.log(project);
        return(
            <li key={id} className="ProjectListItem">
                <ProjectListItem name={project.name} id={id} colorId={project.color.id} changeSelectedProject={props.changeSelectedProject} openDelete={props.openDelete} openEdit={props.openEdit}/>
            </li>
        );
    }) : [];

    return(
        <ul className="ProjectList">
            {projectListItems}
            <li>
                <button id="add-project" onClick={() => props.openCreate(true)}>+ Add Project</button>
            </li>
        </ul>
    );
}

function ProjectListItem(props) {
    const style = reactcss({
        'default': {
            projectColor: {
                background: savedColors[props.colorId].color,
            }
        }
    });

    return(
        <>
            <Link to={"/project/" + props.id}>
                    <button>
                        {props.name}
                        {props.colorId !== "0" && <div id="project-color" style={style.projectColor}/>}
                    </button>
            </Link>
            <button onClick={() => {props.changeSelectedProject(props.id); props.openEdit(true);}}>...</button>
            <button onClick={() => {props.changeSelectedProject(props.id); props.openDelete(true);}}>X</button>
        </>
    );
}
